import React from "react";
import { Box, IconButton, Tooltip, Typography } from "@mui/material";
import SettingsBackupRestoreIcon from "@mui/icons-material/SettingsBackupRestore";

const CompletedTask = ({ tasksList, handleRestore }) => {
  const CompletedTask = () => {
    return tasksList?.map((task, ind) => {
      if (task?.status === "completed") {
        return (
          <Box
            padding="10px"
            display="flex"
            justifyContent="space-between"
            gap="20px"
          >
            <Box display="flex" justifyContent="space-between" gap="20px">
              <Typography>{ind + 1 + ". "}</Typography>
              <Typography>{task?.task}</Typography>
              <Typography>
                {"Completed @ " + task?.createdAt.toLocaleTimeString()}
              </Typography>
            </Box>
            <Box>
              <IconButton
                sx={{ color: "black" }}
                color="primary"
                onClick={() => handleRestore(task)}
              >
                <Tooltip title="Restore" arrow>
                  <SettingsBackupRestoreIcon />
                </Tooltip>
              </IconButton>
            </Box>
          </Box>
        );
      }
    });
  };
  return (
    <>
      <Box width="100%" minHeight="400px" border="2px solid #479">
        <Box backgroundColor="#479">
          <Typography variant="h4" textAlign="center" color="#fff">
            Completed Task
          </Typography>
        </Box>
        <Box>{CompletedTask()}</Box>
      </Box>
    </>
  );
};
export default CompletedTask;
