import React from "react"; // Importing the React library for creating React components.
import { Box, IconButton, Tooltip, Typography } from "@mui/material"; // Importing various Material-UI components for building the user interface.
import SettingsBackupRestoreIcon from "@mui/icons-material/SettingsBackupRestore"; // Importing an icon component.

// The CompletedTask component takes two props: tasksList and handleRestore.
const CompletedTask = ({ tasksList, handleRestore }) => {
  // The renderCompletedTask function is responsible for rendering completed tasks.
  const renderCompletedTask = () => {
    return tasksList?.map((task, ind) => {
      if (task?.status === "completed") {
        return (
          // For each completed task, a Box component is rendered to display task information.
          <Box
            key={ind}
            padding="10px"
            display="flex"
            justifyContent="space-between"
            gap="20px"
          >
            <Typography>{ind + 1 + ". "}</Typography>{" "}
            {/* Displays the task number. */}
            <Typography>{task?.task}</Typography>{" "}
            {/* Displays the task description. */}
            <Typography>
              {"Completed @ " + task?.createdAt.toLocaleTimeString()}{" "}
              {/* Displays the completion time. */}
            </Typography>
            <IconButton
              sx={{ color: "black" }}
              color="primary"
              onClick={() => handleRestore(task)}
            >
              <Tooltip title="Restore" arrow>
                <SettingsBackupRestoreIcon />{" "}
                {/* Displays the restore icon with a tooltip. */}
              </Tooltip>
            </IconButton>
          </Box>
        );
      }
    });
  };

  return (
    <Box width="100%" minHeight="400px" border="2px solid #479">
      {" "}
      {/* A container for the CompletedTask component. */}
      <Box backgroundColor="#479">
        {" "}
        {/* A header section with a background color. */}
        <Typography variant="h4" textAlign="center" color="#fff">
          Completed Task {/* Displays the title "Completed Task". */}
        </Typography>
        <hr /> {/* Adds a horizontal line separator. */}
      </Box>
      <Box display="flex" justifyContent="space-around">
        {" "}
        {/* Header for the task table. */}
        <Typography variant="h6" color="#000">
          S.no & Title {/* Header for the serial number and title column. */}
        </Typography>
        <Typography variant="h6" color="#000">
          completed-Time {/* Header for the completed time column. */}
        </Typography>
        <Typography variant="h6" color="#000">
          Icons {/* Header for the icons column. */}
        </Typography>
      </Box>
      <Box>{renderCompletedTask()}</Box>{" "}
      {/* Calls the renderCompletedTask function to display completed tasks. */}
    </Box>
  );
};

export default CompletedTask; // Exports the CompletedTask component as the default export.
