import { Box, IconButton, Tooltip, Typography } from "@mui/material";
import React from "react";
import TaskAltIcon from "@mui/icons-material/TaskAlt";
import DeleteIcon from "@mui/icons-material/Delete";
import EditIcon from "@mui/icons-material/Edit";

const PendingTask = ({
  taskList,
  handleComplete,
  handleDelete,
  handleEdit,
  selectedTask,
}) => {
  const showTask = () => {
    return taskList?.map((task, ind) => {
      if (task?.status === "pending") {
        return (
          <Box
            padding="10px"
            display="flex"
            justifyContent="space-between"
            gap="20px"
          >
            <Box display="flex" justifyContent="space-between" gap="20px">
              <Typography>{ind + 1 + ". "}</Typography>
              <Typography>{task?.task}</Typography>
              <Typography>{task?.createdAt}</Typography>
            </Box>
            <Box>
              <IconButton
                sx={{ color: "black" }}
                color="primary"
                onClick={() => handleEdit(task)}
              >
                <Tooltip title="Edit_Task" arrow>
                  <EditIcon />
                </Tooltip>
              </IconButton>
              <IconButton
                sx={{ color: "black" }}
                disabled={
                  selectedTask && selectedTask.id === task.id ? true : false
                }
                color="primary"
                onClick={() => handleComplete(task)}
              >
                <Tooltip title="Complete" arrow>
                  <TaskAltIcon />
                </Tooltip>
              </IconButton>
              <IconButton
                sx={{ color: "black" }}
                color="primary"
                disabled={
                  selectedTask && selectedTask.id === task.id ? true : false
                }
                onClick={() => handleDelete(task)}
              >
                <Tooltip title="Delete" arrow>
                  <DeleteIcon />
                </Tooltip>
              </IconButton>
            </Box>
          </Box>
        );
      }
    });
  };

  return (
    <Box
      width="100%"
      minHeight="400px"
      border="2px solid #479"
      display="flex"
      flexDirection="column"
    >
      <Box backgroundColor="#479">
        <Typography variant="h4" textAlign="center" color="#fff">
          Pending Task
        </Typography>
      </Box>

      <Box display="flex" justifyContent={"space-around"}>
        <Typography variant="h6" color="#000">
          S.no&Title
        </Typography>

        <Box gap="  2px">
          {" "}
          <Typography variant="h6" color="#000">
            created time
          </Typography>
        </Box>
        <Box marginX="  30px">
          {" "}
          <Typography variant="h6" color="#000">
            icons
          </Typography>
        </Box>
      </Box>

      <Box>{showTask()}</Box>
    </Box>
  );
};

export default PendingTask;
