import React from "react";
import { Box, IconButton, Tooltip, Typography } from "@mui/material";
import SettingsBackupRestoreIcon from "@mui/icons-material/SettingsBackupRestore";

const CompletedTask = ({ tasksList, handleRestore }) => {
  const renderCompletedTask = () => {
    return tasksList?.map((task, ind) => {
      if (task?.status === "completed") {
        return (
          <Box
            key={ind}
            padding="3px"
            display="flex"
            justifyContent="space-around"
          >
            <Typography>{ind + 1 + "."}</Typography>
            <Typography>{task?.task}</Typography>
            <Typography>
              {(task.createdAt = new Date().toLocaleTimeString())}
            </Typography>
            <IconButton
              sx={{ color: "black" }}
              color="primary"
              onClick={() => handleRestore(task)}
            >
              <Tooltip title="Restore" arrow>
                <SettingsBackupRestoreIcon />
              </Tooltip>
            </IconButton>
          </Box>
        );
      }
    });
  };

  return (/////////////////////////////////////////////mmmmmmmmmmm
    <Box
      width="100%"
      minHeight="400px"
      border="2px solid #479"
      display={"flex"}
      flexDirection={"column"}
    >
      <Box backgroundColor="#479">
        <Typography variant="h4" textAlign="center" color="#fff">
          Completed Task
        </Typography>{" "}
      </Box>
      <Box display="flex" justifyContent="space-around">
        <Typography variant="h6" color="#000">
          S.no & Title <hr />
        </Typography>
        <Typography variant="h6" color="#000">
          completed-Time <hr />
        </Typography>
        <Typography variant="h6" color="#000">
          Icons <hr />
        </Typography>
      </Box>
      <Box>{renderCompletedTask()}</Box>
    </Box>
  );
};

export default CompletedTask;
