import { Box, IconButton, Tooltip, Typography } from '@mui/material'
import React from 'react'
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';

const PendingTask = ({ taskList, handleComplete, handleDelete, handleEdit,selectedTask }) => {

    const showTask = () => {
        return taskList?.map((task, ind) => {
            if (task?.status === 'pending') {
                return (
                    <Box padding='10px' display='flex' justifyContent='space-between' gap='20px'>
                        <Box display='flex' justifyContent='space-between' gap='20px'>
                            <Typography>
                                {ind + 1 +'. '}
                            </Typography>
                            <Typography>
                                {task?.task} 
                            </Typography>
                            <Typography>
                                {task?.createdAt}
                                </Typography>
                        </Box>
                        <Box>
                            <IconButton sx={{ color: 'black' }} color="primary" onClick={() => handleEdit(task)}><Tooltip title="Edit_Task" arrow>
                                <EditIcon />
                                </Tooltip>
                            </IconButton>
                            <IconButton sx={{ color: 'black' }} disabled={selectedTask && selectedTask.id===task.id ? true : false} color="primary" onClick={() => handleComplete(task)}>
                                <Tooltip title="Complete" arrow>
                                <CheckCircleIcon />
                                </Tooltip>
                            </IconButton>
                            <IconButton sx={{ color: 'black' }} color="primary" disabled={selectedTask && selectedTask.id===task.id ? true : false}  onClick={() => handleDelete(task)}>
                                <Tooltip title='Delete' arrow>
                                <DeleteIcon />
                                </Tooltip>
                            </IconButton>
                        </Box>
                    </Box>
                )
            }
        })
    }

    return (
        <Box width='100%' minHeight='400px' border='2px solid #622d97' >
            <Box backgroundColor='#622d97'>
                <Typography variant='h4' textAlign='center' color='#fff'>
                    Pending Task
                </Typography>
            </Box>
            <Box>
                {
                    showTask()
                }
            </Box>
        </Box>
    )
}

export default PendingTask

